@tailwind base;
@tailwind components;
@tailwind utilities;

/* Cybersecurity Theme - Red & Black */
:root {
  --cyber-red: #dc2626;
  --cyber-red-dark: #991b1b;
  --cyber-red-light: #ef4444;
  --cyber-black: #0a0a0a;
  --cyber-gray: #1f1f1f;
  --cyber-gray-light: #2a2a2a;
  --cyber-border: #dc2626;
}

.dark {
  --background: 10 10 10;
  --foreground: 240 10% 95%;
  --card: 20 20 20;
  --card-foreground: 240 10% 95%;
  --popover: 20 20 20;
  --popover-foreground: 240 10% 95%;
  --primary: 346 77% 49%;
  --primary-foreground: 355.7 100% 97.3%;
  --secondary: 240 4% 16%;
  --secondary-foreground: 240 5% 84%;
  --muted: 240 4% 16%;
  --muted-foreground: 240 5% 64%;
  --accent: 12 6% 15%;
  --accent-foreground: 240 5% 84%;
  --destructive: 0 63% 31%;
  --destructive-foreground: 240 10% 95%;
  --border: 346 77% 49%;
  --input: 240 4% 16%;
  --ring: 346 77% 49%;
  --radius: 0.5rem;
}

/* Cybersecurity animations */
@keyframes cyber-pulse {
  0%, 100% {
    box-shadow: 0 0 20px rgba(220, 38, 38, 0.5);
  }
  50% {
    box-shadow: 0 0 40px rgba(220, 38, 38, 0.8);
  }
}

@keyframes data-flow {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

@keyframes security-scan {
  0%, 100% {
    border-color: rgba(220, 38, 38, 0.3);
  }
  50% {
    border-color: rgba(220, 38, 38, 0.8);
  }
}

.cyber-pulse {
  animation: cyber-pulse 2s ease-in-out infinite;
}

.data-flow {
  animation: data-flow 3s linear infinite;
}

.security-scan {
  animation: security-scan 2s ease-in-out infinite;
}

/* Cybersecurity gradient backgrounds */
.cyber-gradient {
  background: linear-gradient(135deg, 
    rgba(220, 38, 38, 0.1) 0%, 
    rgba(0, 0, 0, 0.8) 50%, 
    rgba(220, 38, 38, 0.1) 100%);
}

.threat-gradient {
  background: linear-gradient(45deg, 
    rgba(220, 38, 38, 0.2) 0%, 
    rgba(153, 27, 27, 0.3) 100%);
}

.secure-gradient {
  background: linear-gradient(45deg, 
    rgba(34, 197, 94, 0.2) 0%, 
    rgba(21, 128, 61, 0.3) 100%);
}

.alert-gradient {
  background: linear-gradient(45deg, 
    rgba(245, 158, 11, 0.2) 0%, 
    rgba(217, 119, 6, 0.3) 100%);
}

/* Glass effect with cybersecurity theme */
.cyber-glass {
  backdrop-filter: blur(20px);
  background: rgba(10, 10, 10, 0.9);
  border: 1px solid rgba(220, 38, 38, 0.3);
}

/* Enhanced button styles for security theme */
.btn-cyber {
  position: relative;
  overflow: hidden;
  background: linear-gradient(45deg, #dc2626, #991b1b);
  transition: all 0.3s ease;
  border: 1px solid #dc2626;
}

.btn-cyber:hover {
  background: linear-gradient(45deg, #ef4444, #dc2626);
  box-shadow: 0 0 20px rgba(220, 38, 38, 0.5);
}

/* Security status indicators */
.status-critical {
  color: #dc2626;
  background: rgba(220, 38, 38, 0.1);
  border: 1px solid rgba(220, 38, 38, 0.3);
}

.status-warning {
  color: #f59e0b;
  background: rgba(245, 158, 11, 0.1);
  border: 1px solid rgba(245, 158, 11, 0.3);
}

.status-secure {
  color: #22c55e;
  background: rgba(34, 197, 94, 0.1);
  border: 1px solid rgba(34, 197, 94, 0.3);
}

.btn-cyber::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.btn-cyber:hover::before {
  left: 100%;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: rgba(15, 23, 42, 0.5);
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, #8b5cf6, #06b6d4);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(180deg, #7c3aed, #0891b2);
}

/* Card hover effects */
.card-hover {
  transition: all 0.3s ease;
}

.card-hover:hover {
  transform: translateY(-5px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

/* Text glow effects */
.text-glow {
  text-shadow: 0 0 10px currentColor;
}

.text-glow-purple {
  text-shadow: 0 0 20px rgba(147, 51, 234, 0.8);
}

.text-glow-cyan {
  text-shadow: 0 0 20px rgba(6, 182, 212, 0.8);
}

/* Status indicators */
.status-indicator {
  position: relative;
}

.status-indicator::after {
  content: '';
  position: absolute;
  top: 50%;
  right: -20px;
  transform: translateY(-50%);
  width: 8px;
  height: 8px;
  border-radius: 50%;
  animation: pulse 2s infinite;
}

.status-open::after {
  background-color: #3b82f6;
}

.status-claimed::after {
  background-color: #f59e0b;
}

.status-approved::after {
  background-color: #10b981;
}

.status-rejected::after {
  background-color: #ef4444;
}

/* Loading animations */
.cyber-loader {
  width: 40px;
  height: 40px;
  border: 3px solid rgba(147, 51, 234, 0.3);
  border-top: 3px solid #8b5cf6;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Particle background effect */
.particles {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  pointer-events: none;
}

.particle {
  position: absolute;
  width: 2px;
  height: 2px;
  background: #8b5cf6;
  border-radius: 50%;
  animation: particle-float 20s infinite linear;
  opacity: 0.6;
}

@keyframes particle-float {
  0% {
    transform: translateY(100vh) translateX(0);
    opacity: 0;
  }
  10% {
    opacity: 0.6;
  }
  90% {
    opacity: 0.6;
  }
  100% {
    transform: translateY(-100vh) translateX(100px);
    opacity: 0;
  }
}

/* Enhanced form styles */
.form-field {
  position: relative;
}

.form-field input:focus + label,
.form-field input:not(:placeholder-shown) + label {
  transform: translateY(-20px) scale(0.8);
  color: #8b5cf6;
}

.form-field label {
  position: absolute;
  top: 50%;
  left: 12px;
  transform: translateY(-50%);
  transition: all 0.3s ease;
  pointer-events: none;
  background: rgba(15, 23, 42, 0.8);
  padding: 0 4px;
}

/* Network visualization styles */
.network-node {
  position: relative;
  display: inline-block;
}

.network-node::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 40px;
  height: 40px;
  border: 2px solid #8b5cf6;
  border-radius: 50%;
  animation: ripple 2s infinite;
}

@keyframes ripple {
  0% {
    width: 40px;
    height: 40px;
    opacity: 1;
  }
  100% {
    width: 80px;
    height: 80px;
    opacity: 0;
  }
}

/* dSOC Cybersecurity Platform Design System - RED/BLACK THEME */

@layer base {
  :root {
    /* Main theme - Deep black with red security accents */
    --background: 0 0% 4%;
    --foreground: 0 0% 95%;

    --card: 0 0% 8%;
    --card-foreground: 0 0% 95%;

    --popover: 0 0% 6%;
    --popover-foreground: 0 0% 95%;

    /* Primary - Security red */
    --primary: 0 84% 50%;
    --primary-foreground: 0 0% 98%;
    --primary-glow: 0 84% 65%;

    /* Secondary - Dark red */
    --secondary: 0 20% 12%;
    --secondary-foreground: 0 0% 95%;

    /* Muted - Dark grays */
    --muted: 0 0% 10%;
    --muted-foreground: 0 0% 60%;

    /* Accent - Bright red for highlights */
    --accent: 0 100% 60%;
    --accent-foreground: 0 0% 4%;

    /* Status colors - Security themed */
    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;
    --success: 120 60% 45%;
    --success-foreground: 0 0% 98%;
    --warning: 45 100% 50%;
    --warning-foreground: 0 0% 4%;

    /* Form elements - Dark theme */
    --border: 0 20% 20%;
    --input: 0 0% 12%;
    --ring: 0 84% 50%;

    /* Gradients - Red/Black theme */
    --gradient-primary: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--primary-glow)));
    --gradient-security: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--accent)));
    --gradient-card: linear-gradient(135deg, hsl(var(--card)), hsl(var(--secondary)));
    --gradient-danger: linear-gradient(135deg, hsl(0 84% 30%), hsl(0 84% 50%));

    /* Shadows - Red glow effects */
    --shadow-glow: 0 0 30px hsl(var(--primary) / 0.4);
    --shadow-card: 0 4px 20px hsl(var(--background) / 0.8);
    --shadow-accent: 0 0 20px hsl(var(--accent) / 0.5);

    /* Animation */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);

    --radius: 0.75rem;
  }

  /* Light mode (for accessibility) */
  .light {
    --background: 210 40% 98%;
    --foreground: 210 24% 16%;

    --card: 0 0% 100%;
    --card-foreground: 210 24% 16%;

    --popover: 0 0% 100%;
    --popover-foreground: 210 24% 16%;

    --primary: 200 100% 35%;
    --primary-foreground: 210 40% 98%;

    --secondary: 210 40% 96%;
    --secondary-foreground: 210 24% 16%;

    --muted: 210 40% 96%;
    --muted-foreground: 210 20% 45%;

    --accent: 180 100% 40%;
    --accent-foreground: 210 40% 98%;

    --border: 210 31% 91%;
    --input: 210 31% 91%;
    --ring: 200 100% 35%;

    --shadow-glow: 0 0 30px hsl(var(--primary) / 0.2);
    --shadow-card: 0 4px 20px hsl(var(--foreground) / 0.1);
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-muted;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-primary/50 rounded-full;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-primary;
  }
}

@layer components {
  /* Security-themed components */
  .security-card {
    @apply bg-gradient-to-br from-card to-secondary border border-border/50 backdrop-blur-sm;
    box-shadow: var(--shadow-card);
  }

  .security-button {
    @apply bg-gradient-to-r from-primary to-accent text-primary-foreground transition-all duration-300;
    box-shadow: var(--shadow-glow);
  }

  .security-button:hover {
    box-shadow: var(--shadow-accent);
  }

  .status-badge {
    @apply px-3 py-1 rounded-full text-xs font-medium;
  }

  .status-open {
    @apply bg-warning/20 text-warning border border-warning/30;
  }

  .status-in-progress {
    @apply bg-primary/20 text-primary border border-primary/30;
  }

  .status-completed {
    @apply bg-success/20 text-success border border-success/30;
  }

  .status-escalated {
    @apply bg-destructive/20 text-destructive border border-destructive/30;
  }

  /* Animated elements */
  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  @keyframes pulse-glow {
    0%, 100% {
      box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
    }
    50% {
      box-shadow: 0 0 30px hsl(var(--primary) / 0.6);
    }
  }

  .slide-in {
    animation: slide-in 0.3s ease-out;
  }

  @keyframes slide-in {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }
}

