import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useWallet } from "@/components/WalletProvider";
import { evmContractService } from "@/lib/evm-contract";
import { 
  Bot, 
  Upload, 
  Shield, 
  AlertTriangle, 
  FileText, 
  Zap, 
  CheckCircle,
  Clock,
  Database,
  Users
} from "lucide-react";

interface AIAnalysisResult {
  title: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  category: string;
  description: string;
  technicalDetails: string;
  recommendations: string[];
  estimatedReward: number;
  requiredAnalysts: number;
}

export default function AISubmitCaseModal({ children }: { children: React.ReactNode }) {
  const { walletType, evmAddress, iotaAddress, isEVMConnected, isIOTAConnected } = useWallet();
  const { toast } = useToast();

  const [isOpen, setIsOpen] = useState(false);
  const [step, setStep] = useState<'input' | 'analyzing' | 'review' | 'uploading' | 'success'>('input');
  const [userInput, setUserInput] = useState('');
  const [caseType, setCaseType] = useState('');
  const [analysisResult, setAnalysisResult] = useState<AIAnalysisResult | null>(null);
  const [blockchainTxHash, setBlockchainTxHash] = useState('');

  const isConnected = walletType === 'iota' ? isIOTAConnected : isEVMConnected;
  const currentAddress = walletType === 'iota' ? iotaAddress : evmAddress;

  const caseTypes = [
    { value: 'vulnerability', label: 'Security Vulnerability' },
    { value: 'breach', label: 'Data Breach' },
    { value: 'malware', label: 'Malware Detection' },
    { value: 'phishing', label: 'Phishing Attack' },
    { value: 'ddos', label: 'DDoS Attack' },
    { value: 'insider_threat', label: 'Insider Threat' },
    { value: 'compliance', label: 'Compliance Violation' },
    { value: 'other', label: 'Other Security Incident' }
  ];

  const severityColors = {
    low: 'bg-green-500/20 text-green-400 border-green-500/30',
    medium: 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30', 
    high: 'bg-orange-500/20 text-orange-400 border-orange-500/30',
    critical: 'bg-red-500/20 text-red-400 border-red-500/30'
  };

  const analyzeWithAI = async () => {
    if (!userInput || !caseType) {
      toast({
        title: "Missing Information",
        description: "Please provide case details and select a case type",
        variant: "destructive"
      });
      return;
    }

    setStep('analyzing');

    try {
      const response = await fetch('/api/ai/analyze-case', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userInput,
          caseType,
          requestType: 'security_case_analysis'
        }),
      });

      if (!response.ok) {
        throw new Error('AI analysis failed');
      }

      const result = await response.json();

      // Parse AI response into structured case data
      const analysis: AIAnalysisResult = {
        title: result.title || `${caseType.charAt(0).toUpperCase() + caseType.slice(1)} Security Case`,
        severity: result.severity || 'medium',
        category: result.category || caseType,
        description: result.description || userInput,
        technicalDetails: result.technicalDetails || result.analysis || 'AI analysis pending...',
        recommendations: result.recommendations || ['Further investigation required'],
        estimatedReward: result.estimatedReward || 100,
        requiredAnalysts: result.requiredAnalysts || 2
      };

      setAnalysisResult(analysis);
      setStep('review');

    } catch (error: any) {
      toast({
        title: "AI Analysis Failed",
        description: error.message || "Failed to analyze the security case",
        variant: "destructive"
      });
      setStep('input');
    }
  };

  const uploadToBlockchain = async () => {
    if (!analysisResult || !isConnected) {
      toast({
        title: "Cannot Upload",
        description: "Missing analysis result or wallet not connected",
        variant: "destructive"
      });
      return;
    }

    setStep('uploading');

    try {
      // Create blockchain ticket data
      const caseData = {
        title: analysisResult.title,
        description: analysisResult.description,
        severity: analysisResult.severity,
        technicalDetails: analysisResult.technicalDetails,
        category: analysisResult.category,
        recommendations: analysisResult.recommendations,
        submittedBy: currentAddress,
        submissionType: 'ai_generated_case',
        timestamp: new Date().toISOString()
      };

      let txHash = '';

      if (walletType === 'evm') {
        // Submit to EVM blockchain via smart contract
        const tx = await evmContractService.createTicket();
        const receipt = await tx.wait();
        txHash = receipt.transactionHash;

        // Also store detailed case data via backend API
        const caseResponse = await fetch('/api/ai-cases', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...caseData,
            blockchainTxHash: txHash,
            network: 'scroll_evm',
            estimatedReward: analysisResult.estimatedReward,
            requiredAnalysts: analysisResult.requiredAnalysts
          }),
        });

        if (!caseResponse.ok) {
          console.warn('Failed to store case details in backend');
        }

      } else {
        // Submit to IOTA blockchain (simulated for now)
        txHash = '0x' + Math.random().toString(16).substr(2, 8);

        // Store case data via backend API
        const caseResponse = await fetch('/api/ai-cases', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            ...caseData,
            blockchainTxHash: txHash,
            network: 'iota',
            estimatedReward: analysisResult.estimatedReward,
            requiredAnalysts: analysisResult.requiredAnalysts
          }),
        });

        if (!caseResponse.ok) {
          console.warn('Failed to store case details in backend');
        }
      }

      setBlockchainTxHash(txHash);
      setStep('success');

      toast({
        title: "Case Uploaded Successfully",
        description: `AI case uploaded to ${walletType === 'iota' ? 'IOTA' : 'Scroll EVM'} blockchain`,
      });

      // Trigger notification to analysts
      window.dispatchEvent(new CustomEvent('newAICaseUploaded', { 
        detail: { 
          ...analysisResult, 
          txHash,
          network: walletType 
        } 
      }));

    } catch (error: any) {
      toast({
        title: "Upload Failed",
        description: error.message || "Failed to upload case to blockchain",
        variant: "destructive"
      });
      setStep('review');
    }
  };

  const resetModal = () => {
    setStep('input');
    setUserInput('');
    setCaseType('');
    setAnalysisResult(null);
    setBlockchainTxHash('');
  };

  const handleClose = () => {
    setIsOpen(false);
    setTimeout(resetModal, 300); // Reset after animation
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>

      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto bg-slate-800 border-purple-500/30">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-white">
            <Bot className="h-6 w-6 text-purple-400" />
            AI-Powered Case Submission
          </DialogTitle>
          <DialogDescription className="text-gray-400">
            Describe your security incident and let AI analyze it, then upload to blockchain for analyst investigation
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Progress Indicator */}
          <div className="flex items-center justify-between text-sm">
            <div className={`flex items-center gap-2 ${step === 'input' ? 'text-purple-400' : step === 'analyzing' || step === 'review' || step === 'uploading' || step === 'success' ? 'text-green-400' : 'text-gray-500'}`}>
              <div className={`w-3 h-3 rounded-full ${step === 'input' ? 'bg-purple-400' : step === 'analyzing' || step === 'review' || step === 'uploading' || step === 'success' ? 'bg-green-400' : 'bg-gray-500'}`} />
              Input Details
            </div>
            <div className={`flex items-center gap-2 ${step === 'analyzing' ? 'text-purple-400' : step === 'review' || step === 'uploading' || step === 'success' ? 'text-green-400' : 'text-gray-500'}`}>
              <div className={`w-3 h-3 rounded-full ${step === 'analyzing' ? 'bg-purple-400' : step === 'review' || step === 'uploading' || step === 'success' ? 'bg-green-400' : 'bg-gray-500'}`} />
              AI Analysis
            </div>
            <div className={`flex items-center gap-2 ${step === 'review' ? 'text-purple-400' : step === 'uploading' || step === 'success' ? 'text-green-400' : 'text-gray-500'}`}>
              <div className={`w-3 h-3 rounded-full ${step === 'review' ? 'bg-purple-400' : step === 'uploading' || step === 'success' ? 'bg-green-400' : 'bg-gray-500'}`} />
              Review & Edit
            </div>
            <div className={`flex items-center gap-2 ${step === 'uploading' ? 'text-purple-400' : step === 'success' ? 'text-green-400' : 'text-gray-500'}`}>
              <div className={`w-3 h-3 rounded-full ${step === 'uploading' ? 'bg-purple-400' : step === 'success' ? 'bg-green-400' : 'bg-gray-500'}`} />
              Blockchain Upload
            </div>
            <div className={`flex items-center gap-2 ${step === 'success' ? 'text-green-400' : 'text-gray-500'}`}>
              <div className={`w-3 h-3 rounded-full ${step === 'success' ? 'bg-green-400' : 'bg-gray-500'}`} />
              Complete
            </div>
          </div>

          {/* Step 1: Input Details */}
          {step === 'input' && (
            <div className="space-y-4">
              <div>
                <Label htmlFor="caseType" className="text-gray-300">
                  Case Type *
                </Label>
                <Select value={caseType} onValueChange={setCaseType}>
                  <SelectTrigger className="bg-slate-700 border-purple-500/30 text-white">
                    <SelectValue placeholder="Select the type of security incident" />
                  </SelectTrigger>
                  <SelectContent className="bg-slate-800 border-purple-500/30">
                    {caseTypes.map((type) => (
                      <SelectItem key={type.value} value={type.value}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="userInput" className="text-gray-300">
                  Describe the Security Incident *
                </Label>
                <Textarea
                  id="userInput"
                  placeholder="Describe what happened, when it occurred, what systems were affected, and any evidence you have..."
                  value={userInput}
                  onChange={(e) => setUserInput(e.target.value)}
                  className="bg-slate-700 border-purple-500/30 text-white min-h-[120px]"
                />
                <div className="text-xs text-gray-500 mt-1">
                  Be as detailed as possible. AI will analyze this to create a comprehensive case report.
                </div>
              </div>

              <div className="flex justify-end gap-3">
                <Button variant="outline" onClick={handleClose}>
                  Cancel
                </Button>
                <Button 
                  onClick={analyzeWithAI}
                  disabled={!userInput || !caseType}
                  className="bg-purple-600 hover:bg-purple-700"
                >
                  <Bot className="h-4 w-4 mr-2" />
                  Analyze with AI
                </Button>
              </div>
            </div>
          )}

          {/* Step 2: AI Analysis in Progress */}
          {step === 'analyzing' && (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-purple-400 mx-auto mb-4"></div>
              <h3 className="text-lg font-semibold text-white mb-2">AI Analyzing Case...</h3>
              <p className="text-gray-400">
                Analyzing security incident details and generating comprehensive case report
              </p>
            </div>
          )}

          {/* Step 3: Review AI Analysis */}
          {step === 'review' && analysisResult && (
            <div className="space-y-6">
              <Card className="bg-slate-700 border-purple-500/30">
                <CardHeader>
                  <CardTitle className="text-white flex items-center gap-2">
                    <FileText className="h-5 w-5" />
                    AI-Generated Case Report
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid md:grid-cols-2 gap-4">
                    <div>
                      <Label className="text-gray-300">Title</Label>
                      <Input
                        value={analysisResult.title}
                        onChange={(e) => setAnalysisResult({...analysisResult, title: e.target.value})}
                        className="bg-slate-600 border-purple-500/30 text-white mt-1"
                      />
                    </div>
                    <div>
                      <Label className="text-gray-300">Severity</Label>
                      <div className="mt-1">
                        <Badge className={severityColors[analysisResult.severity]}>
                          {analysisResult.severity.toUpperCase()}
                        </Badge>
                      </div>
                    </div>
                  </div>

                  <div>
                    <Label className="text-gray-300">Description</Label>
                    <Textarea
                      value={analysisResult.description}
                      onChange={(e) => setAnalysisResult({...analysisResult, description: e.target.value})}
                      className="bg-slate-600 border-purple-500/30 text-white mt-1"
                    />
                  </div>

                  <div>
                    <Label className="text-gray-300">Technical Analysis</Label>
                    <Textarea
                      value={analysisResult.technicalDetails}
                      onChange={(e) => setAnalysisResult({...analysisResult, technicalDetails: e.target.value})}
                      className="bg-slate-600 border-purple-500/30 text-white mt-1 min-h-[100px]"
                    />
                  </div>

                  <div className="grid md:grid-cols-2 gap-4">
                    <div>
                      <Label className="text-gray-300">Estimated Reward (CLT)</Label>
                      <Input
                        type="number"
                        value={analysisResult.estimatedReward}
                        onChange={(e) => setAnalysisResult({...analysisResult, estimatedReward: parseInt(e.target.value)})}
                        className="bg-slate-600 border-purple-500/30 text-white mt-1"
                      />
                    </div>
                    <div>
                      <Label className="text-gray-300">Required Analysts</Label>
                      <Input
                        type="number"
                        value={analysisResult.requiredAnalysts}
                        onChange={(e) => setAnalysisResult({...analysisResult, requiredAnalysts: parseInt(e.target.value)})}
                        className="bg-slate-600 border-purple-500/30 text-white mt-1"
                      />
                    </div>
                  </div>
                </CardContent>
              </Card>

              <div className="flex justify-end gap-3">
                <Button variant="outline" onClick={() => setStep('input')}>
                  Back to Edit
                </Button>
                <Button 
                  onClick={uploadToBlockchain}
                  disabled={!isConnected}
                  className="bg-green-600 hover:bg-green-700"
                >
                  <Upload className="h-4 w-4 mr-2" />
                  Upload to Blockchain
                </Button>
              </div>
            </div>
          )}

          {/* Step 4: Uploading to Blockchain */}
          {step === 'uploading' && (
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-green-400 mx-auto mb-4"></div>
              <h3 className="text-lg font-semibold text-white mb-2">Uploading to Blockchain...</h3>
              <p className="text-gray-400">
                Creating case ticket on {walletType === 'iota' ? 'IOTA' : 'Scroll EVM'} network and notifying analysts
              </p>
            </div>
          )}

          {/* Step 5: Success */}
          {step === 'success' && (
            <div className="text-center py-8">
              <CheckCircle className="h-16 w-16 text-green-400 mx-auto mb-4" />
              <h3 className="text-xl font-bold text-white mb-2">Case Successfully Uploaded!</h3>
              <p className="text-gray-400 mb-6">
                Your AI-analyzed security case has been uploaded to the blockchain and security analysts will begin investigation.
              </p>

              <div className="space-y-4">
                <Card className="bg-slate-700 border-green-500/30">
                  <CardContent className="pt-6">
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div className="text-center">
                        <Database className="h-6 w-6 text-green-400 mx-auto mb-2" />
                        <div className="text-gray-300">Blockchain</div>
                        <div className="text-white font-medium">
                          {walletType === 'iota' ? 'IOTA Network' : 'Scroll EVM'}
                        </div>
                      </div>
                      <div className="text-center">
                        <Users className="h-6 w-6 text-purple-400 mx-auto mb-2" />
                        <div className="text-gray-300">Analysts Needed</div>
                        <div className="text-white font-medium">{analysisResult?.requiredAnalysts}</div>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                {blockchainTxHash && (
                  <div className="text-xs text-gray-500 break-all">
                    Transaction: {blockchainTxHash}
                  </div>
                )}
              </div>

              <div className="flex justify-center gap-3 mt-6">
                <Button variant="outline" onClick={handleClose}>
                  Close
                </Button>
                <Button 
                  onClick={() => window.location.href = '/pools'}
                  className="bg-purple-600 hover:bg-purple-700"
                >
                  View All Cases
                </Button>
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/BlockchainSelector.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useWallet } from "@/components/WalletProvider";
import { ConnectButton } from "@iota/dapp-kit";
import { useToast } from "@/hooks/use-toast";
import { Network, Wallet, ShieldCheck, Zap } from "lucide-react";

interface BlockchainSelectorProps {
  isOpen: boolean;
  onClose: () => void;
  onConnected: () => void;
}

export default function BlockchainSelector({ isOpen, onClose, onConnected }: BlockchainSelectorProps) {
  const { walletType, setWalletType, connectEVMWallet, isEVMConnected, isIOTAConnected } = useWallet();
  const [connecting, setConnecting] = useState(false);
  const { toast } = useToast();

  const blockchains = [
    {
      id: 'iota' as const,
      name: 'IOTA',
      description: 'Feeless and sustainable blockchain',
      icon: Network,
      color: 'text-blue-400',
      bgColor: 'bg-blue-500/10',
      features: [
        'Zero transaction fees',
        'Environmentally friendly',
        'Quantum resistant',
        'Fast finality'
      ],
      isConnected: isIOTAConnected
    },
    {
      id: 'evm' as const,
      name: 'Scroll (EVM)',
      description: 'Ethereum Layer 2 scaling solution',
      icon: ShieldCheck,
      color: 'text-orange-400',
      bgColor: 'bg-orange-500/10',
      features: [
        'EVM compatible',
        'Low gas fees',
        'High throughput',
        'Ethereum security'
      ],
      isConnected: isEVMConnected
    }
  ];

  const handleIOTAConnect = () => {
    setWalletType('iota');
    // IOTA connection is handled by the ConnectButton component
  };

  const handleEVMConnect = async () => {
    setConnecting(true);
    try {
      setWalletType('evm');
      await connectEVMWallet();
      toast({
        title: "EVM Wallet Connected",
        description: "Successfully connected to Scroll Sepolia",
      });
      onConnected();
      onClose();
    } catch (error: any) {
      toast({
        title: "Connection Failed",
        description: error.message || "Failed to connect EVM wallet",
        variant: "destructive",
      });
    } finally {
      setConnecting(false);
    }
  };

  const handleBlockchainSelect = (blockchainId: 'iota' | 'evm') => {
    if (blockchainId === 'iota') {
      handleIOTAConnect();
    } else {
      handleEVMConnect();
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[600px] bg-slate-900 border-purple-500/20">
        <DialogHeader>
          <DialogTitle className="text-white flex items-center gap-2">
            <Wallet className="h-5 w-5" />
            Choose Your Blockchain
          </DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 p-4">
          {blockchains.map((blockchain) => {
            const Icon = blockchain.icon;
            return (
              <Card
                key={blockchain.id}
                className={`cursor-pointer transition-all hover:scale-105 bg-slate-800 border-purple-500/20 hover:border-purple-500/40 ${
                  walletType === blockchain.id ? 'ring-2 ring-purple-500' : ''
                }`}
                onClick={() => setWalletType(blockchain.id)}
              >
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <div className={`p-2 rounded-lg ${blockchain.bgColor}`}>
                        <Icon className={`h-6 w-6 ${blockchain.color}`} />
                      </div>
                      <div>
                        <CardTitle className="text-white text-lg">{blockchain.name}</CardTitle>
                        <p className="text-gray-400 text-sm">{blockchain.description}</p>
                      </div>
                    </div>
                    {blockchain.isConnected && (
                      <Badge className="bg-green-500/20 text-green-400 border-green-500/30">
                        Connected
                      </Badge>
                    )}
                  </div>
                </CardHeader>

                <CardContent className="pt-0">
                  <div className="space-y-2 mb-4">
                    {blockchain.features.map((feature) => (
                      <div key={feature} className="flex items-center gap-2 text-sm text-gray-300">
                        <Zap className="h-3 w-3 text-purple-400" />
                        {feature}
                      </div>
                    ))}
                  </div>

                  {blockchain.id === 'iota' && walletType === 'iota' && (
                    <div className="w-full">
                      <ConnectButton
                        className="w-full bg-blue-600 hover:bg-blue-700 text-white"
                        connectText="Connect IOTA Wallet"
                      />
                    </div>
                  )}

                  {blockchain.id === 'evm' && walletType === 'evm' && (
                    <Button
                      onClick={handleEVMConnect}
                      disabled={connecting || blockchain.isConnected}
                      className="w-full bg-orange-600 hover:bg-orange-700"
                    >
                      {connecting ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                          Connecting...
                        </>
                      ) : blockchain.isConnected ? (
                        'EVM Connected'
                      ) : (
                        'Connect EVM Wallet'
                      )}
                    </Button>
                  )}

                  {walletType !== blockchain.id && (
                    <Button
                      variant="outline"
                      onClick={() => setWalletType(blockchain.id)}
                      className="w-full border-purple-500/30 text-gray-300 hover:bg-purple-500/10"
                    >
                      Select {blockchain.name}
                    </Button>
                  )}
                </CardContent>
              </Card>
            );
          })}
        </div>

        <div className="p-4 border-t border-purple-500/20">
          <p className="text-sm text-gray-400 text-center mb-4">
            Choose your blockchain network. IOTA and EVM are completely separate - transactions will only go to your selected network.
          </p>

          {(isIOTAConnected || isEVMConnected) && (
            <div className="flex justify-center">
              <Button
                onClick={() => {
                  onConnected();
                  onClose();
                }}
                className="bg-purple-600 hover:bg-purple-700"
              >
                Enter Platform
              </Button>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/EVMStakingRewards..content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Badge } from "./ui/badge";
import { useToast } from "@/hooks/use-toast";
import { evmContractService } from "@/lib/evm-contract";
import { useWallet } from "./WalletProvider";
import { 
  Coins, 
  TrendingUp, 
  Award, 
  ArrowUpCircle, 
  ArrowDownCircle,
  Target,
  Lock,
  DollarSign,
  Activity,
  ExternalLink
} from "lucide-react";

interface EVMStakingData {
  stakedAmount: number;
  rewardDebt: number;
  cltBalance: number;
  rewardRate: number;
}

export default function EVMStakingRewards() {
  const [stakingData, setStakingData] = useState<EVMStakingData>({
    stakedAmount: 0,
    rewardDebt: 0,
    cltBalance: 0,
    rewardRate: 0
  });

  const [stakeAmount, setStakeAmount] = useState("100");
  const [withdrawAmount, setWithdrawAmount] = useState("50");
  const [isLoading, setIsLoading] = useState(true);
  const [isStaking, setIsStaking] = useState(false);
  const [isWithdrawing, setIsWithdrawing] = useState(false);
  const [isClaiming, setIsClaiming] = useState(false);

  const { evmAddress, isEVMConnected } = useWallet();
  const { toast } = useToast();

  useEffect(() => {
    if (isEVMConnected && evmAddress) {
      loadStakingData();
    }
  }, [isEVMConnected, evmAddress]);

  const loadStakingData = async () => {
    if (!evmAddress) return;

    try {
      setIsLoading(true);

      // Get CLT balance
      const cltBalanceBN = await evmContractService.getCLTBalance(evmAddress);
      const cltBalance = parseFloat(evmContractService.formatCLT(cltBalanceBN));

      // Get staking info
      const stakeInfo = await evmContractService.getStakeInfo(evmAddress);

      const stakedAmount = parseFloat(evmContractService.formatCLT(stakeInfo.amount));
      const rewardDebt = parseFloat(evmContractService.formatCLT(stakeInfo.rewardDebt));

      setStakingData({
        stakedAmount,
        rewardDebt,
        cltBalance,
        rewardRate: 5.5 // This could be fetched from contract
      });
    } catch (error) {
      console.error('Error loading staking data:', error);
      toast({
        title: "Error",
        description: "Failed to load staking data",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleStake = async () => {
    if (!stakeAmount || isNaN(Number(stakeAmount))) {
      toast({
        title: "Error",
        description: "Please enter a valid stake amount",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsStaking(true);
      const amount = evmContractService.parseCLT(stakeAmount);
      const tx = await evmContractService.stake(stakeAmount);
      const receipt = await tx.wait();
      const txHash = receipt.transactionHash;

      if (txHash) {
        toast({
          title: "Success!",
          description: (
            <div className="flex items-center gap-2">
              <span>Stake transaction submitted</span>
              <ExternalLink 
                className="h-4 w-4 cursor-pointer" 
                onClick={() => window.open(`https://sepolia.scrollscan.com/tx/${txHash}`, '_blank')}
              />
            </div>
          ),
        });

        // Reload data after successful stake
        setTimeout(() => {
          loadStakingData();
        }, 2000);

        setStakeAmount("100");
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to stake tokens",
        variant: "destructive",
      });
    } finally {
      setIsStaking(false);
    }
  };

  const handleWithdraw = async () => {
    if (!withdrawAmount || isNaN(Number(withdrawAmount))) {
      toast({
        title: "Error",
        description: "Please enter a valid withdraw amount",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsWithdrawing(true);
      const amount = evmContractService.parseCLT(withdrawAmount);
      const tx = await evmContractService.withdraw(withdrawAmount);
      const receipt = await tx.wait();
      const txHash = receipt.transactionHash;

      if (txHash) {
        toast({
          title: "Success!",
          description: (
            <div className="flex items-center gap-2">
              <span>Withdrawal transaction submitted</span>
              <ExternalLink 
                className="h-4 w-4 cursor-pointer" 
                onClick={() => window.open(`https://sepolia.scrollscan.com/tx/${txHash}`, '_blank')}
              />
            </div>
          ),
        });

        // Reload data after successful withdrawal
        setTimeout(() => {
          loadStakingData();
        }, 2000);

        setWithdrawAmount("50");
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to withdraw stake",
        variant: "destructive",
      });
    } finally {
      setIsWithdrawing(false);
    }
  };

  const handleClaimRewards = async () => {
    try {
      setIsClaiming(true);
      const txHash = await evmContractService.claimRewards();

      if (txHash) {
        toast({
          title: "Success!",
          description: (
            <div className="flex items-center gap-2">
              <span>Rewards claimed successfully</span>
              <ExternalLink 
                className="h-4 w-4 cursor-pointer" 
                onClick={() => window.open(`https://sepolia.scrollscan.com/tx/${txHash}`, '_blank')}
              />
            </div>
          ),
        });

        // Reload data after successful claim
        setTimeout(() => {
          loadStakingData();
        }, 2000);
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to claim rewards",
        variant: "destructive",
      });
    } finally {
      setIsClaiming(false);
    }
  };

  if (!isEVMConnected) {
    return (
      <Card className="bg-slate-800/50 border-red-500/30 backdrop-blur-sm">
        <CardContent className="p-8 text-center">
          <div className="w-16 h-16 bg-red-500/20 rounded-full flex items-center justify-center mx-auto mb-4">
            <Lock className="h-8 w-8 text-red-400" />
          </div>
          <h3 className="text-xl font-semibold text-red-400 mb-2">EVM Wallet Not Connected</h3>
          <p className="text-gray-400">Please connect your MetaMask wallet to access EVM staking features</p>
        </CardContent>
      </Card>
    );
  }

  if (isLoading) {
    return (
      <Card className="bg-slate-800/50 border-purple-500/30 backdrop-blur-sm">
        <CardContent className="p-8 text-center">
          <div className="w-8 h-8 border-2 border-purple-400 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-400">Loading EVM staking data...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Network Info */}
      <Card className="bg-gradient-to-r from-green-900/20 to-blue-900/20 border-green-500/30 backdrop-blur-sm">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Activity className="h-6 w-6 text-green-400" />
              <div>
                <CardTitle className="text-green-400">Scroll Sepolia Testnet</CardTitle>
                <CardDescription className="text-gray-400">
                  EVM-compatible staking on Scroll L2
                </CardDescription>
              </div>
            </div>
            <Badge className="bg-green-500/20 text-green-400 border-green-500/30">
              Live
            </Badge>
          </div>
        </CardHeader>
      </Card>

      {/* Staking Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="bg-slate-800/50 border-blue-500/30 backdrop-blur-sm">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-blue-400">CLT Balance</CardTitle>
            <Coins className="h-4 w-4 text-blue-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stakingData.cltBalance.toLocaleString()}</div>
            <p className="text-xs text-gray-400">Available CLT tokens</p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800/50 border-green-500/30 backdrop-blur-sm">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-green-400">Staked Amount</CardTitle>
            <Lock className="h-4 w-4 text-green-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stakingData.stakedAmount.toLocaleString()}</div>
            <p className="text-xs text-gray-400">CLT tokens staked</p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800/50 border-yellow-500/30 backdrop-blur-sm">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-yellow-400">Reward Debt</CardTitle>
            <Award className="h-4 w-4 text-yellow-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stakingData.rewardDebt.toLocaleString()}</div>
            <p className="text-xs text-gray-400">Accumulated rewards</p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800/50 border-purple-500/30 backdrop-blur-sm">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium text-purple-400">APR</CardTitle>
            <TrendingUp className="h-4 w-4 text-purple-400" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white">{stakingData.rewardRate}%</div>
            <p className="text-xs text-gray-400">Annual percentage rate</p>
          </CardContent>
        </Card>
      </div>

      {/* Staking Actions */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Stake CLT */}
        <Card className="bg-slate-800/50 border-green-500/30 backdrop-blur-sm">
          <CardHeader>
            <CardTitle className="text-green-400 flex items-center gap-3">
              <ArrowUpCircle className="h-5 w-5" />
              Stake CLT Tokens
            </CardTitle>
            <CardDescription className="text-gray-400">
              Stake your CLT tokens to earn rewards
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="stakeAmount" className="text-gray-300">Amount to Stake</Label>
              <Input
                id="stakeAmount"
                type="number"
                value={stakeAmount}
                onChange={(e) => setStakeAmount(e.target.value)}
                className="bg-slate-700/50 border-gray-600 text-white"
                placeholder="Enter CLT amount"
              />
            </div>
            <Button
              onClick={handleStake}
              disabled={isStaking || !stakeAmount}
              className="w-full bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800"
            >
              {isStaking ? (
                <div className="flex items-center gap-2">
                  <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                  Staking...
                </div>
              ) : (
                <div className="flex items-center gap-2">
                  <ArrowUpCircle className="h-4 w-4" />
                  Stake {stakeAmount} CLT
                </div>
              )}
            </Button>
          </CardContent>
        </Card>

        {/* Withdraw & Claim */}
        <Card className="bg-slate-800/50 border-orange-500/30 backdrop-blur-sm">
          <CardHeader>
            <CardTitle className="text-orange-400 flex items-center gap-3">
              <ArrowDownCircle className="h-5 w-5" />
              Withdraw & Claim
            </CardTitle>
            <CardDescription className="text-gray-400">
              Withdraw your staked tokens or claim rewards
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="withdrawAmount" className="text-gray-300">Amount to Withdraw</Label>
              <Input
                id="withdrawAmount"
                type="number"
                value={withdrawAmount}
                onChange={(e) => setWithdrawAmount(e.target.value)}
                className="bg-slate-700/50 border-gray-600 text-white"
                placeholder="Enter CLT amount"
              />
            </div>

            <div className="grid grid-cols-2 gap-3">
              <Button
                onClick={handleWithdraw}
                disabled={isWithdrawing || !withdrawAmount || stakingData.stakedAmount === 0}
                variant="outline"
                className="border-orange-500/30 text-orange-400 hover:bg-orange-500/10"
              >
                {isWithdrawing ? (
                  <div className="flex items-center gap-2">
                    <div className="w-4 h-4 border-2 border-orange-400/30 border-t-orange-400 rounded-full animate-spin" />
                    Withdrawing...
                  </div>
                ) : (
                  <div className="flex items-center gap-2">
                    <ArrowDownCircle className="h-4 w-4" />
                    Withdraw
                  </div>
                )}
              </Button>

              <Button
                onClick={handleClaimRewards}
                disabled={isClaiming || stakingData.rewardDebt === 0}
                className="bg-gradient-to-r from-yellow-600 to-yellow-700 hover:from-yellow-700 hover:to-yellow-800"
              >
                {isClaiming ? (
                  <div className="flex items-center gap-2">
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                    Claiming...
                  </div>
                ) : (
                  <div className="flex items-center gap-2">
                    <Award className="h-4 w-4" />
                    Claim
                  </div>
                )}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Contract Information */}
      <Card className="bg-slate-800/50 border-gray-600/30 backdrop-blur-sm">
        <CardHeader>
          <CardTitle className="text-gray-300">Contract Information</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
            <div>
              <p className="text-gray-400">CLT Token:</p>
              <p className="text-white font-mono break-all">0xBb647745eFfFD6a950d08cE6Dddc6D6c308D1403</p>
            </div>
            <div>
              <p className="text-gray-400">Staking Pool:</p>
              <p className="text-white font-mono break-all">0xB480FA23e8d586Af034aae3CA9a0D111E071a01e</p>
            </div>
            <div>
              <p className="text-gray-400">SOC Service:</p>
              <p className="text-white font-mono break-all">0x284B4cE9027b8f81211efd19A3a5D40D8b232D60</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

