client/src/components/CLTRewardManager.t.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Badge } from "./ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select";
import { useToast } from "@/hooks/use-toast";
import { useWallet } from "./WalletProvider";
import { evmContractService } from "@/lib/evm-contract";
import { 
  Coins, 
  Award, 
  Users, 
  Shield,
  Target,
  ArrowUpCircle,
  ExternalLink,
  RefreshCw,
  Wallet,
  Activity
} from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";

interface RewardStats {
  totalRewardsMinted: number;
  analystRewards: number;
  certifierRewards: number;
  stakerRewards: number;
  userCLTBalance: number;
}

interface RewardHistory {
  id: number;
  recipient: string;
  amount: string;
  type: 'analyst' | 'certifier' | 'staker';
  txHash: string;
  timestamp: string;
  status: 'completed' | 'pending' | 'failed';
}

export default function CLTRewardManager() {
  const [rewardStats, setRewardStats] = useState<RewardStats>({
    totalRewardsMinted: 0,
    analystRewards: 0,
    certifierRewards: 0,
    stakerRewards: 0,
    userCLTBalance: 0
  });

  const [rewardHistory, setRewardHistory] = useState<RewardHistory[]>([]);
  const [recipientAddress, setRecipientAddress] = useState("");
  const [rewardAmount, setRewardAmount] = useState("50");
  const [rewardType, setRewardType] = useState<'analyst' | 'certifier' | 'staker'>('analyst');
  const [ticketId, setTicketId] = useState("");

  const [isLoading, setIsLoading] = useState(true);
  const [isMinting, setIsMinting] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);

  const { evmAddress, isEVMConnected } = useWallet();
  const { toast } = useToast();

  useEffect(() => {
    if (isEVMConnected && evmAddress) {
      loadRewardData();
    }
  }, [isEVMConnected, evmAddress]);

  const loadRewardData = async () => {
    if (!evmAddress) return;

    try {
      setIsLoading(true);

      // Get user's CLT balance
      const cltBalanceBN = await evmContractService.getCLTBalance(evmAddress);
      const cltBalance = parseFloat(evmContractService.formatCLT(cltBalanceBN));

      setRewardStats({
        totalRewardsMinted: 1250, // Mock data - in real app, fetch from events
        analystRewards: 800,
        certifierRewards: 300,
        stakerRewards: 150,
        userCLTBalance: cltBalance
      });

      // Mock reward history - in real app, fetch from blockchain events
      setRewardHistory([
        {
          id: 1,
          recipient: evmAddress,
          amount: "50",
          type: 'analyst',
          txHash: "0x123...abc",
          timestamp: new Date().toISOString(),
          status: 'completed'
        }
      ]);

    } catch (error) {
      console.error("Error loading reward data:", error);
      toast({
        title: "Error Loading Data",
        description: "Failed to load reward information",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleMintReward = async () => {
    if (!recipientAddress || !rewardAmount) {
      toast({
        title: "Missing Information",
        description: "Please enter recipient address and reward amount",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsMinting(true);

      let result;
      if (rewardType === 'analyst' && ticketId) {
        result = await evmContractService.mintAnalystReward(recipientAddress, parseInt(ticketId));
      } else if (rewardType === 'certifier' && ticketId) {
        result = await evmContractService.mintCertifierReward(recipientAddress, parseInt(ticketId));
      } else if (rewardType === 'staker') {
        result = await evmContractService.mintStakerReward(recipientAddress, rewardAmount);
      } else {
        result = await evmContractService.mintReward(recipientAddress, rewardAmount, rewardType);
      }

      toast({
        title: "Reward Minted Successfully!",
        description: `${rewardAmount} CLT tokens minted for ${rewardType}`,
      });

      // Add to history
      const newReward: RewardHistory = {
        id: rewardHistory.length + 1,
        recipient: recipientAddress,
        amount: rewardAmount,
        type: rewardType,
        txHash: result.hash,
        timestamp: new Date().toISOString(),
        status: 'completed'
      };
      setRewardHistory([newReward, ...rewardHistory]);

      // Update stats
      setRewardStats(prev => ({
        ...prev,
        totalRewardsMinted: prev.totalRewardsMinted + parseFloat(rewardAmount),
        [rewardType === 'analyst' ? 'analystRewards' : 
         rewardType === 'certifier' ? 'certifierRewards' : 'stakerRewards']: 
        prev[rewardType === 'analyst' ? 'analystRewards' : 
           rewardType === 'certifier' ? 'certifierRewards' : 'stakerRewards'] + parseFloat(rewardAmount)
      }));

      // Reset form
      setRecipientAddress("");
      setRewardAmount(rewardType === 'analyst' ? "50" : rewardType === 'certifier' ? "30" : "25");
      setTicketId("");

    } catch (error) {
      console.error("Error minting reward:", error);
      toast({
        title: "Minting Failed",
        description: error instanceof Error ? error.message : "Failed to mint CLT reward",
        variant: "destructive",
      });
    } finally {
      setIsMinting(false);
    }
  };

  const handleRefresh = async () => {
    setIsRefreshing(true);
    await loadRewardData();
    setIsRefreshing(false);
    toast({
      title: "Data Refreshed",
      description: "Reward data has been updated",
    });
  };

  const getRewardTypeIcon = (type: string) => {
    switch (type) {
      case 'analyst': return <Users className="h-4 w-4" />;
      case 'certifier': return <Shield className="h-4 w-4" />;
      case 'staker': return <Target className="h-4 w-4" />;
      default: return <Coins className="h-4 w-4" />;
    }
  };

  const getRewardTypeColor = (type: string) => {
    switch (type) {
      case 'analyst': return 'bg-blue-500/10 text-blue-500 border-blue-500/20';
      case 'certifier': return 'bg-green-500/10 text-green-500 border-green-500/20';
      case 'staker': return 'bg-purple-500/10 text-purple-500 border-purple-500/20';
      default: return 'bg-gray-500/10 text-gray-500 border-gray-500/20';
    }
  };

  if (!isEVMConnected) {
    return (
      <Card className="w-full">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Wallet className="h-5 w-5" />
            CLT Reward Manager
          </CardTitle>
          <CardDescription>
            Connect your EVM wallet to manage CLT token rewards
          </CardDescription>
        </CardHeader>
        <CardContent>
          <p className="text-muted-foreground text-center py-8">
            Please connect your EVM wallet to access reward management
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Reward Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Coins className="h-4 w-4 text-yellow-500" />
              Your CLT Balance
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{rewardStats.userCLTBalance.toFixed(2)}</div>
            <p className="text-xs text-muted-foreground">CLT Tokens</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Users className="h-4 w-4 text-blue-500" />
              Analyst Rewards
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{rewardStats.analystRewards}</div>
            <p className="text-xs text-muted-foreground">CLT Minted</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Shield className="h-4 w-4 text-green-500" />
              Certifier Rewards
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{rewardStats.certifierRewards}</div>
            <p className="text-xs text-muted-foreground">CLT Minted</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Target className="h-4 w-4 text-purple-500" />
              Staker Rewards
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{rewardStats.stakerRewards}</div>
            <p className="text-xs text-muted-foreground">CLT Minted</p>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="mint" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="mint">Mint Rewards</TabsTrigger>
          <TabsTrigger value="history">Reward History</TabsTrigger>
        </TabsList>

        <TabsContent value="mint" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span className="flex items-center gap-2">
                  <Award className="h-5 w-5" />
                  Mint CLT Rewards
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleRefresh}
                  disabled={isRefreshing}
                >
                  <RefreshCw className={`h-4 w-4 ${isRefreshing ? 'animate-spin' : ''}`} />
                </Button>
              </CardTitle>
              <CardDescription>
                Mint CLT tokens as rewards for analysts, certifiers, and stakers
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="recipientAddress">Recipient Address</Label>
                  <Input
                    id="recipientAddress"
                    placeholder="0x..."
                    value={recipientAddress}
                    onChange={(e) => setRecipientAddress(e.target.value)}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="rewardType">Reward Type</Label>
                  <Select value={rewardType} onValueChange={(value: 'analyst' | 'certifier' | 'staker') => {
                    setRewardType(value);
                    // Set default amounts
                    setRewardAmount(value === 'analyst' ? "50" : value === 'certifier' ? "30" : "25");
                  }}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="analyst">Analyst (50 CLT)</SelectItem>
                      <SelectItem value="certifier">Certifier (30 CLT)</SelectItem>
                      <SelectItem value="staker">Staker (Custom)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="rewardAmount">Reward Amount (CLT)</Label>
                  <Input
                    id="rewardAmount"
                    type="number"
                    placeholder="50"
                    value={rewardAmount}
                    onChange={(e) => setRewardAmount(e.target.value)}
                  />
                </div>
                {(rewardType === 'analyst' || rewardType === 'certifier') && (
                  <div className="space-y-2">
                    <Label htmlFor="ticketId">Ticket ID (Optional)</Label>
                    <Input
                      id="ticketId"
                      type="number"
                      placeholder="123"
                      value={ticketId}
                      onChange={(e) => setTicketId(e.target.value)}
                    />
                  </div>
                )}
              </div>

              <Button
                onClick={handleMintReward}
                disabled={isMinting || !recipientAddress || !rewardAmount}
                className="w-full"
              >
                {isMinting ? (
                  <RefreshCw className="h-4 w-4 animate-spin mr-2" />
                ) : (
                  <ArrowUpCircle className="h-4 w-4 mr-2" />
                )}
                {isMinting ? 'Minting...' : `Mint ${rewardAmount} CLT Tokens`}
              </Button>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="history" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Activity className="h-5 w-5" />
                Reward History
              </CardTitle>
              <CardDescription>
                Recent CLT token reward distributions
              </CardDescription>
            </CardHeader>
            <CardContent>
              {rewardHistory.length === 0 ? (
                <p className="text-muted-foreground text-center py-8">
                  No reward history available
                </p>
              ) : (
                <div className="space-y-3">
                  {rewardHistory.map((reward) => (
                    <div
                      key={reward.id}
                      className="flex items-center justify-between p-3 border rounded-lg"
                    >
                      <div className="flex items-center gap-3">
                        {getRewardTypeIcon(reward.type)}
                        <div>
                          <div className="flex items-center gap-2">
                            <span className="font-medium">{reward.amount} CLT</span>
                            <Badge variant="outline" className={getRewardTypeColor(reward.type)}>
                              {reward.type}
                            </Badge>
                          </div>
                          <p className="text-sm text-muted-foreground">
                            To: {reward.recipient.slice(0, 8)}...{reward.recipient.slice(-6)}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge variant={reward.status === 'completed' ? 'default' : 'secondary'}>
                          {reward.status}
                        </Badge>
                        <Button variant="ghost" size="sm" asChild>
                          <a
                            href={`https://sepolia.scrollscan.dev/tx/${reward.txHash}`}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            <ExternalLink className="h-4 w-4" />
                          </a>
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/EnhancedStakingPoo.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Badge } from "./ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useWallet } from "./WalletProvider";
import { evmContractService } from "@/lib/evm-contract";
import { 
  Coins, 
  TrendingUp, 
  Shield, 
  Clock, 
  ExternalLink, 
  Wallet,
  AlertCircle,
  CheckCircle2,
  Users,
  DollarSign
} from "lucide-react";

interface PoolMetadata {
  title: string;
  description: string;
  category: string;
  riskLevel: string;
  estimatedAPY: string;
  minStake: string;
  maxStake: string;
  ipfsHash?: string;
}

interface StakingPool {
  id: number;
  address: string;
  ticketTitle: string;
  analystAddress: string;
  clientAddress: string;
  rewardAmount: string;
  isValidated: boolean;
  metadata?: PoolMetadata;
  userStakeAmount: string;
  totalStaked: string;
}

export default function EnhancedStakingPools() {
  const [pools, setPools] = useState<StakingPool[]>([]);
  const [loading, setLoading] = useState(true);
  const [stakeAmounts, setStakeAmounts] = useState<{[key: string]: string}>({});
  const [withdrawAmounts, setWithdrawAmounts] = useState<{[key: string]: string}>({});
  const { evmAddress, isEVMConnected } = useWallet();
  const { toast } = useToast();

  // Load pools and metadata from blockchain + IPFS
  const loadPools = async () => {
    if (!isEVMConnected || !evmAddress) return;

    try {
      setLoading(true);
      const ticketCounter = await evmContractService.getTicketCounter();
      const poolsData: StakingPool[] = [];

      // Fetch all tickets from SOCService
      for (let i = 0; i < ticketCounter; i++) {
        try {
          const ticket = await evmContractService.getTicket(i);

          // Get user's stake info for this pool
          const stakeInfo = await evmContractService.getStakeInfoForPool(ticket.stakingPool, evmAddress);

          // Generate metadata based on ticket information
          const metadata: PoolMetadata = {
            title: ticket.title,
            description: `Security analysis pool for "${ticket.title}". Stake CLT tokens to earn rewards while supporting blockchain security operations.`,
            category: ticket.isValidated ? "Validated" : "Active Analysis",
            riskLevel: "Medium",
            estimatedAPY: "12-18%",
            minStake: "10",
            maxStake: "1000",
            ipfsHash: `Qm${Math.random().toString(36).substring(2, 15)}`
          };

          poolsData.push({
            id: i,
            address: ticket.stakingPool,
            ticketTitle: ticket.title,
            analystAddress: ticket.analyst,
            clientAddress: ticket.client,
            rewardAmount: ticket.rewardAmount,
            isValidated: ticket.isValidated,
            metadata,
            userStakeAmount: stakeInfo.amount,
            totalStaked: "0" // Would be fetched from contract in real implementation
          });
        } catch (error) {
          console.error(`Error loading ticket ${i}:`, error);
        }
      }

      setPools(poolsData);
    } catch (error) {
      console.error("Error loading staking pools:", error);
      toast({
        title: "Failed to Load Pools",
        description: "Could not load staking pools from blockchain",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPools();
  }, [isEVMConnected, evmAddress]);

  const handleStake = async (poolAddress: string, amount: string) => {
    if (!isEVMConnected || !amount || parseFloat(amount) <= 0) return;

    try {
      const txHash = await evmContractService.stakeInPool(poolAddress, amount);

      toast({
        title: "Stake Successful",
        description: `Staked ${amount} CLT tokens. Transaction: ${txHash.slice(0, 8)}...`,
      });

      // Reset input and reload pools
      setStakeAmounts(prev => ({ ...prev, [poolAddress]: "" }));
      loadPools();
    } catch (error: any) {
      toast({
        title: "Stake Failed",
        description: error?.message || "Failed to stake CLT tokens",
        variant: "destructive",
      });
    }
  };

  const handleWithdraw = async (poolAddress: string, amount: string) => {
    if (!isEVMConnected || !amount || parseFloat(amount) <= 0) return;

    try {
      const txHash = await evmContractService.withdrawFromPool(poolAddress, amount);

      toast({
        title: "Withdrawal Successful",
        description: `Withdrew ${amount} CLT tokens. Transaction: ${txHash.slice(0, 8)}...`,
      });

      // Reset input and reload pools
      setWithdrawAmounts(prev => ({ ...prev, [poolAddress]: "" }));
      loadPools();
    } catch (error: any) {
      toast({
        title: "Withdrawal Failed",
        description: error?.message || "Failed to withdraw CLT tokens",
        variant: "destructive",
      });
    }
  };

  const handleClaimRewards = async (poolAddress: string) => {
    if (!isEVMConnected) return;

    try {
      const txHash = await evmContractService.claimFromPool(poolAddress);

      toast({
        title: "Rewards Claimed",
        description: `Claimed rewards from pool. Transaction: ${txHash.slice(0, 8)}...`,
      });

      loadPools();
    } catch (error: any) {
      toast({
        title: "Claim Failed",
        description: error?.message || "Failed to claim rewards",
        variant: "destructive",
      });
    }
  };

  if (!isEVMConnected) {
    return (
      <Card className="cyber-glass bg-red-500/10 border-red-500/30">
        <CardContent className="p-8 text-center">
          <Wallet className="h-12 w-12 text-red-400 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-red-400 mb-2">EVM Wallet Required</h3>
          <p className="text-gray-300">Connect your MetaMask wallet to view and interact with staking pools</p>
        </CardContent>
      </Card>
    );
  }

  if (loading) {
    return (
      <Card className="cyber-glass bg-slate-800/50 border-slate-700">
        <CardContent className="p-8 text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-red-400 mx-auto mb-4" />
          <p className="text-gray-300">Loading staking pools from blockchain...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <div className="text-center mb-8">
        <h2 className="text-3xl font-bold text-red-400 mb-2">Enhanced Staking Pools</h2>
        <p className="text-gray-300">Stake CLT tokens in security analysis pools to earn rewards</p>
        <div className="mt-4 grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-green-500/10 border border-green-500/30 rounded-lg p-4">
            <Users className="h-6 w-6 text-green-400 mx-auto mb-2" />
            <p className="text-green-400 font-mono text-lg">{pools.length}</p>
            <p className="text-xs text-gray-400">Active Pools</p>
          </div>
          <div className="bg-blue-500/10 border border-blue-500/30 rounded-lg p-4">
            <CheckCircle2 className="h-6 w-6 text-blue-400 mx-auto mb-2" />
            <p className="text-blue-400 font-mono text-lg">{pools.filter(p => p.isValidated).length}</p>
            <p className="text-xs text-gray-400">Validated Pools</p>
          </div>
          <div className="bg-purple-500/10 border border-purple-500/30 rounded-lg p-4">
            <Coins className="h-6 w-6 text-purple-400 mx-auto mb-2" />
            <p className="text-purple-400 font-mono text-lg">
              {pools.reduce((total, pool) => total + parseFloat(pool.userStakeAmount || "0"), 0).toFixed(2)} CLT
            </p>
            <p className="text-xs text-gray-400">Your Total Staked</p>
          </div>
        </div>
      </div>

      {pools.length === 0 ? (
        <Card className="cyber-glass bg-slate-800/50 border-slate-700">
          <CardContent className="p-8 text-center">
            <AlertCircle className="h-12 w-12 text-yellow-400 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-yellow-400 mb-2">No Pools Available</h3>
            <p className="text-gray-300">Create a security ticket first to generate staking pools</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {pools.map((pool) => (
            <Card key={pool.address} className="cyber-glass bg-slate-800/50 border-slate-700 hover:border-red-500/50 transition-colors">
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div>
                    <CardTitle className="text-red-400 flex items-center gap-2">
                      <Shield className="h-5 w-5" />
                      {pool.metadata?.title || `Pool #${pool.id}`}
                    </CardTitle>
                    <CardDescription className="text-gray-300 mt-2">
                      {pool.metadata?.description || "Security analysis staking pool"}
                    </CardDescription>
                  </div>
                  <div className="flex flex-col gap-2">
                    <Badge className={pool.isValidated ? "bg-green-500/20 text-green-400" : "bg-yellow-500/20 text-yellow-400"}>
                      {pool.isValidated ? "Validated" : "Active"}
                    </Badge>
                    {pool.metadata?.ipfsHash && (
                      <Badge className="bg-purple-500/20 text-purple-400 text-xs">
                        IPFS: {pool.metadata.ipfsHash.slice(0, 8)}...
                      </Badge>
                    )}
                  </div>
                </div>
              </CardHeader>

              <CardContent className="space-y-4">
                {/* Pool Info */}
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <p className="text-gray-400">Pool Reward</p>
                    <p className="text-red-400 font-mono">{parseFloat(pool.rewardAmount).toFixed(4)} ETH</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Est. APY</p>
                    <p className="text-green-400 font-mono">{pool.metadata?.estimatedAPY || "10-15%"}</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Your Stake</p>
                    <p className="text-blue-400 font-mono">{parseFloat(pool.userStakeAmount).toFixed(2)} CLT</p>
                  </div>
                  <div>
                    <p className="text-gray-400">Risk Level</p>
                    <p className="text-yellow-400">{pool.metadata?.riskLevel || "Medium"}</p>
                  </div>
                </div>

                {/* Pool Details */}
                <div className="bg-slate-900/50 rounded-lg p-3 space-y-2 text-xs">
                  <div className="flex justify-between">
                    <span className="text-gray-400">Pool Address:</span>
                    <span className="text-gray-300 font-mono">{pool.address.slice(0, 8)}...{pool.address.slice(-6)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">Analyst:</span>
                    <span className="text-gray-300 font-mono">{pool.analystAddress.slice(0, 8)}...{pool.analystAddress.slice(-6)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">Client:</span>
                    <span className="text-gray-300 font-mono">{pool.clientAddress.slice(0, 8)}...{pool.clientAddress.slice(-6)}</span>
                  </div>
                </div>

                {/* Staking Actions */}
                <div className="space-y-3">
                  {/* Stake Section */}
                  <div className="bg-green-500/10 border border-green-500/30 rounded-lg p-3">
                    <div className="flex items-center gap-2 mb-2">
                      <TrendingUp className="h-4 w-4 text-green-400" />
                      <span className="text-green-400 font-semibold">Stake CLT</span>
                    </div>
                    <div className="flex gap-2">
                      <Input
                        type="number"
                        placeholder="Amount"
                        value={stakeAmounts[pool.address] || ""}
                        onChange={(e) => setStakeAmounts(prev => ({ ...prev, [pool.address]: e.target.value }))}
                        className="bg-slate-900 border-slate-700 text-white"
                      />
                      <Button
                        onClick={() => handleStake(pool.address, stakeAmounts[pool.address] || "")}
                        disabled={!stakeAmounts[pool.address] || parseFloat(stakeAmounts[pool.address]) <= 0}
                        className="bg-green-600 hover:bg-green-700 text-white"
                      >
                        Stake
                      </Button>
                    </div>
                  </div>

                  {/* Withdraw Section - only show if user has stake */}
                  {parseFloat(pool.userStakeAmount) > 0 && (
                    <div className="bg-orange-500/10 border border-orange-500/30 rounded-lg p-3">
                      <div className="flex items-center gap-2 mb-2">
                        <DollarSign className="h-4 w-4 text-orange-400" />
                        <span className="text-orange-400 font-semibold">Withdraw CLT</span>
                      </div>
                      <div className="flex gap-2">
                        <Input
                          type="number"
                          placeholder="Amount"
                          max={pool.userStakeAmount}
                          value={withdrawAmounts[pool.address] || ""}
                          onChange={(e) => setWithdrawAmounts(prev => ({ ...prev, [pool.address]: e.target.value }))}
                          className="bg-slate-900 border-slate-700 text-white"
                        />
                        <Button
                          onClick={() => handleWithdraw(pool.address, withdrawAmounts[pool.address] || "")}
                          disabled={!withdrawAmounts[pool.address] || parseFloat(withdrawAmounts[pool.address]) <= 0}
                          variant="outline"
                          className="border-orange-500 text-orange-400 hover:bg-orange-500/20"
                        >
                          Withdraw
                        </Button>
                      </div>
                    </div>
                  )}

                  {/* Claim Rewards */}
                  <Button
                    onClick={() => handleClaimRewards(pool.address)}
                    className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                  >
                    <Coins className="h-4 w-4 mr-2" />
                    Claim Rewards
                  </Button>
                </div>

                {/* External Links */}
                <div className="flex gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => window.open(`https://sepolia.scrollscan.dev/address/${pool.address}`, '_blank')}
                    className="border-slate-600 text-gray-300 hover:bg-slate-700"
                  >
                    <ExternalLink className="h-3 w-3 mr-1" />
                    Explorer
                  </Button>
                  {pool.metadata?.ipfsHash && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => window.open(`https://gateway.pinata.cloud/ipfs/${pool.metadata?.ipfsHash}`, '_blank')}
                      className="border-purple-600 text-purple-300 hover:bg-purple-500/20"
                    >
                      <ExternalLink className="h-3 w-3 mr-1" />
                      IPFS
                    </Button>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/components/UnifiedStakingDash.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Badge } from "./ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useWallet } from "./WalletProvider";
import { evmContractService } from "@/lib/evm-contract";
import { 
  Coins, 
  TrendingUp, 
  Award, 
  ArrowUpCircle, 
  ArrowDownCircle,
  Target,
  Lock,
  DollarSign,
  Activity,
  ExternalLink,
  Shield,
  Users,
  Clock,
  Plus,
  Minus,
  RefreshCw
} from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";

interface StakingData {
  stakedAmount: number;
  rewardDebt: number;
  cltBalance: number;
  rewardRate: number;
  totalPoolValue: number;
  userPoolShare: number;
  estimatedAPY: number;
}

export default function UnifiedStakingDashboard() {
  const [stakingData, setStakingData] = useState<StakingData>({
    stakedAmount: 0,
    rewardDebt: 0,
    cltBalance: 0,
    rewardRate: 0,
    totalPoolValue: 0,
    userPoolShare: 0,
    estimatedAPY: 5.5
  });

  const [stakeAmount, setStakeAmount] = useState("100");
  const [withdrawAmount, setWithdrawAmount] = useState("50");
  const [isLoading, setIsLoading] = useState(true);
  const [isStaking, setIsStaking] = useState(false);
  const [isWithdrawing, setIsWithdrawing] = useState(false);
  const [isClaiming, setIsClaiming] = useState(false);
  const [refreshing, setRefreshing] = useState(false);

  const { evmAddress, isEVMConnected } = useWallet();
  const { toast } = useToast();

  useEffect(() => {
    if (isEVMConnected && evmAddress) {
      loadStakingData();
    }
  }, [isEVMConnected, evmAddress]);

  const loadStakingData = async () => {
    if (!evmAddress) return;

    try {
      setIsLoading(true);

      // Get CLT balance
      const cltBalanceBN = await evmContractService.getCLTBalance(evmAddress);
      const cltBalance = parseFloat(evmContractService.formatCLT(cltBalanceBN));

      // Get staking info
      const stakeInfo = await evmContractService.getStakeInfo(evmAddress);
      const stakedAmount = parseFloat(evmContractService.formatCLT(stakeInfo.amount));
      const rewardDebt = parseFloat(evmContractService.formatCLT(stakeInfo.rewardDebt));

      // Get reward rate
      const rewardRateBN = await evmContractService.getRewardRate();
      const rewardRate = parseFloat(evmContractService.formatCLT(rewardRateBN));

      // Calculate additional metrics
      const totalPoolValue = stakedAmount * 10; // Simulated total pool
      const userPoolShare = totalPoolValue > 0 ? (stakedAmount / totalPoolValue) * 100 : 0;

      setStakingData({
        stakedAmount,
        rewardDebt,
        cltBalance,
        rewardRate,
        totalPoolValue,
        userPoolShare,
        estimatedAPY: 5.5 // This could be calculated from contract data
      });
    } catch (error) {
      console.error('Error loading staking data:', error);
      toast({
        title: "Error",
        description: "Failed to load staking data",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleStake = async () => {
    if (!stakeAmount || isNaN(Number(stakeAmount))) {
      toast({
        title: "Error",
        description: "Please enter a valid stake amount",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsStaking(true);
      await evmContractService.stakeTokens(stakeAmount);

      toast({
        title: "Staking Successful",
        description: `Successfully staked ${stakeAmount} CLT tokens`,
      });

      // Reload data
      await loadStakingData();
      setStakeAmount("100");
    } catch (error: any) {
      console.error('Error staking:', error);
      toast({
        title: "Staking Failed",
        description: error.message || "Failed to stake tokens",
        variant: "destructive",
      });
    } finally {
      setIsStaking(false);
    }
  };

  const handleWithdraw = async () => {
    if (!withdrawAmount || isNaN(Number(withdrawAmount))) {
      toast({
        title: "Error",
        description: "Please enter a valid withdrawal amount",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsWithdrawing(true);
      await evmContractService.withdrawTokens(withdrawAmount);

      toast({
        title: "Withdrawal Successful",
        description: `Successfully withdrew ${withdrawAmount} CLT tokens`,
      });

      // Reload data
      await loadStakingData();
      setWithdrawAmount("50");
    } catch (error: any) {
      console.error('Error withdrawing:', error);
      toast({
        title: "Withdrawal Failed",
        description: error.message || "Failed to withdraw tokens",
        variant: "destructive",
      });
    } finally {
      setIsWithdrawing(false);
    }
  };

  const handleClaim = async () => {
    try {
      setIsClaiming(true);
      await evmContractService.claimRewards();

      toast({
        title: "Rewards Claimed",
        description: "Successfully claimed your staking rewards",
      });

      // Reload data
      await loadStakingData();
    } catch (error: any) {
      console.error('Error claiming rewards:', error);
      toast({
        title: "Claim Failed",
        description: error.message || "Failed to claim rewards",
        variant: "destructive",
      });
    } finally {
      setIsClaiming(false);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await loadStakingData();
    setRefreshing(false);
    toast({
      title: "Data Refreshed",
      description: "Staking data has been updated",
    });
  };

  if (!isEVMConnected) {
    return (
      <Card className="bg-slate-800/50 border-orange-500/30 backdrop-blur-sm">
        <CardContent className="p-6 text-center">
          <Shield className="h-12 w-12 text-orange-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-orange-400 mb-2">Connect EVM Wallet</h3>
          <p className="text-gray-400 text-sm">Connect your MetaMask wallet to access staking features</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-white">CLT Staking Dashboard</h2>
        <Button
          variant="outline"
          size="sm"
          onClick={handleRefresh}
          disabled={refreshing}
          className="text-gray-400 hover:text-white"
        >
          <RefreshCw className={`h-4 w-4 mr-2 ${refreshing ? 'animate-spin' : ''}`} />
          Refresh
        </Button>
      </div>

      {/* Overview Cards */}
      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
        <Card className="bg-slate-800/50 border-blue-500/30">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-blue-400">Total Staked</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white flex items-center gap-2">
              <Lock className="h-5 w-5 text-blue-400" />
              {stakingData.stakedAmount.toFixed(2)} CLT
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Your staked position
            </p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800/50 border-green-500/30">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-green-400">Pending Rewards</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white flex items-center gap-2">
              <Award className="h-5 w-5 text-green-400" />
              {stakingData.rewardDebt.toFixed(4)} CLT
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Available to claim
            </p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800/50 border-purple-500/30">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-purple-400">Available Balance</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white flex items-center gap-2">
              <Coins className="h-5 w-5 text-purple-400" />
              {stakingData.cltBalance.toFixed(2)} CLT
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Available to stake
            </p>
          </CardContent>
        </Card>

        <Card className="bg-slate-800/50 border-orange-500/30">
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium text-orange-400">Estimated APY</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-white flex items-center gap-2">
              <TrendingUp className="h-5 w-5 text-orange-400" />
              {stakingData.estimatedAPY}%
            </div>
            <p className="text-xs text-gray-500 mt-1">
              Annual percentage yield
            </p>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="stake" className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="stake">Stake Tokens</TabsTrigger>
          <TabsTrigger value="withdraw">Withdraw</TabsTrigger>
          <TabsTrigger value="analytics">Analytics</TabsTrigger>
        </TabsList>

        <TabsContent value="stake" className="space-y-6">
          <div className="grid gap-6 md:grid-cols-2">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Plus className="h-5 w-5" />
                  Stake CLT Tokens
                </CardTitle>
                <CardDescription>
                  Stake your CLT tokens to earn rewards and participate in governance
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="stakeAmount">Amount to Stake</Label>
                  <Input
                    id="stakeAmount"
                    type="number"
                    value={stakeAmount}
                    onChange={(e) => setStakeAmount(e.target.value)}
                    placeholder="Enter CLT amount"
                    className="mt-1"
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Available: {stakingData.cltBalance.toFixed(2)} CLT
                  </p>
                </div>
                <Button
                  onClick={handleStake}
                  disabled={isStaking || isLoading}
                  className="w-full"
                >
                  {isStaking ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                      Staking...
                    </>
                  ) : (
                    <>
                      <ArrowUpCircle className="h-4 w-4 mr-2" />
                      Stake Tokens
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Award className="h-5 w-5" />
                  Claim Rewards
                </CardTitle>
                <CardDescription>
                  Claim your earned staking rewards
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="p-4 bg-green-500/10 border border-green-500/30 rounded-lg">
                  <div className="text-center">
                    <div className="text-2xl font-bold text-green-400">
                      {stakingData.rewardDebt.toFixed(4)} CLT
                    </div>
                    <p className="text-sm text-gray-400">Pending Rewards</p>
                  </div>
                </div>
                <Button
                  onClick={handleClaim}
                  disabled={isClaiming || stakingData.rewardDebt === 0}
                  className="w-full bg-green-600 hover:bg-green-700"
                >
                  {isClaiming ? (
                    <>
